import React, { useState, useEffect, useRef } from "react";
import "./Typewriter.css";

const Typewriter = ({ text, baseDelay = 10 }) => {
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0);
  const textDivRef = useRef(null); // Reference to the div

  useEffect(() => {
    if (index < text.length) {
      const delay = baseDelay + Math.random() * 150;
      const timer = setTimeout(() => {
        setDisplayedText((d) => d + text.charAt(index));
        setIndex((prevIndex) => prevIndex + 1);
      }, delay);

      return () => clearTimeout(timer);
    }
  }, [index, text, baseDelay]);

  return (
    <div>
      <h1>Hey, I'm Adam :)</h1>
      <div
        ref={textDivRef}
        className="typewriter"
        contentEditable="false" // Prevent the mobile keyboard from popping up
        style={{
          cursor: "text",
          padding: "10px",
          border: "1px solid #ccc",
          minHeight: "50px",
        }}
      >
        {displayedText}
      </div>
    </div>
  );
};

export default Typewriter;
