import React, { useState, useEffect } from "react";
import "../Components/Typewriter.css"; // Ensure your CSS path is correct

const Typewriter = ({ text, baseDelay = 100 }) => {
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (index < text.length) {
      // Calculate random delay
      const delay = Math.random() * (baseDelay - 30) + 30;
      const timer = setTimeout(() => {
        setDisplayedText(displayedText + text.charAt(index)); // Add next character
        setIndex(index + 1); // Move to the next character
      }, delay);

      return () => clearTimeout(timer);
    }
  }, [index, text, baseDelay]); // Depend on `index` to trigger the next character

  return <p className="typewriter">{displayedText}</p>;
};

export default Typewriter;
