import React, { useState, useEffect } from "react";
import "./Typewriter.css"; // Ensure your CSS is imported

const Typewriter = ({ text, baseDelay = 100 }) => {
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (index < text.length) {
      const delay = Math.random() * (baseDelay - 30) + 30;
      const timer = setTimeout(() => {
        setDisplayedText((d) => d + text.charAt(index)); // Functional update for displayedText
        setIndex(index + 1);
      }, delay);

      return () => clearTimeout(timer);
    }
  }, [index, text, baseDelay]);
  const handleKeyDown = (e) => {
    e.preventDefault(); // Prevents all user input
  };
  return (
    <textarea
      className="typewriter"
      value={displayedText}
      onKeyDown={handleKeyDown} // Prevents typing, cutting, pasting, etc.
      onMouseDown={(e) => e.preventDefault()} // Optional: prevents text selection
      style={{ cursor: "text" }} // Ensures the cursor is always visible as a text cursor
    />
  );
};

export default Typewriter;
